package leetcode1_10;/** * No11_IntegertoRoman * https://leetcode.com/problems/integer-to-roman/ * * @Author zc * @Create 2016-06-30-17:51 */public class No11_IntegertoRoman {    public String intToRoman(int num) {        StringBuffer str = new StringBuffer();        String[] symbol = {"M","CM","D","CD","C","XC","L","XL","X","IX","V","IV","I"};        int[] value=      {1000,900,500,400, 100, 90,  50, 40,  10, 9,   5,  4,   1};        for(int i=0;num!=0;i++)        {            while(num>=value[i])            {                num -= value[i];                str.append(symbol[i]);            }        }        return str.toString();    }}