package leetcode1_10;import java.util.HashMap;/** * No1_TwoNum * https://leetcode.com/problems/two-sum/ * * @Author zc * @Create 2016-06-20-11:51 */public class No1_TwoNum {    public int[] twoSum(int[] nums, int target) {        HashMap<Integer, Integer> map = new HashMap<Integer, Integer>(nums.length,1);        for(int i = 0;i < nums.length; i++) {            Integer except = target - nums[i];            if(map.containsKey(except)) {                return new int[]{map.get(except),i};            }            map.put(nums[i],i);        }        return new int[]{0,0};    }}