package leetcode1_10;/** * No2_AddTwoNumbers * https://leetcode.com/problems/add-two-numbers/ * * @Author zc * @Create 2016-06-20-14:31 */class ListNode {    int val;    ListNode next;    ListNode(int x) { val = x; }}public class No2_AddTwoNumbers {    public ListNode addTwoNumbers(ListNode l1, ListNode l2) {        ListNode listNode = new ListNode(0);        ListNode p = l1,q = l2, cur = listNode;        int carry = 0;        while(p != null || q != null) {            int x = p != null ? p.val : 0;            int y = q != null ? q.val : 0;            int total = x + y + carry;            cur.next = new ListNode(total%10);            carry = total/10;            cur = cur.next;            p = p != null ? p.next : p;            q = q != null ? q.next : q;        }        if(carry > 0) {            cur.next = new ListNode(carry);        }        return listNode.next;    }}