package leetcode1_10;/** * No4_MedianofTwoSortedArrays * https://leetcode.com/problems/median-of-two-sorted-arrays/ * * @Author zc * @Create 2016-06-20-17:50 */public class No4_MedianofTwoSortedArrays {    public double findMedianSortedArrays(int[] nums1, int[] nums2) {        int i = 0,j = 0,l = 0;        int n1 = nums1.length, n2 = nums2.length ,n3 = nums1.length + nums2.length;        int[] arr = new int[n3];        while(i < n1 && j < n2) {            if(nums1[i] < nums2[j]) {                arr[l++] = nums1[i++];            } else {                arr[l++] = nums2[j++];            }        }        while(i < n1) {            arr[l++] = nums1[i++];        }        while(j < n2) {            arr[l++] = nums2[j++];        }        if(n3 % 2 == 0) {            return (arr[n3 / 2] + arr[n3 / 2 - 1]) / 2.0;        } else {            return arr[n3 / 2];        }    }}