package leetcode1_10;/** * No6_ZigZagConversion * https://leetcode.com/problems/zigzag-conversion/ * * @Author zc * @Create 2016-06-24-17:48 */public class No6_ZigZagConversion {    public String convert(String s, int numRows) {        if(numRows == 1) {            return s;        }        StringBuilder sb = new StringBuilder();        char[] arr = s.toCharArray();        for(int row = 0; row < numRows; row++) {            int n = row;            while(n < arr.length) {                sb.append(arr[n]);                if(row == 0 || row == numRows - 1) {                    n = n + (numRows - 1) * 2;                } else {                    n = n + (numRows - 1);                }            }        }        return sb.toString();    }}