package leetcode1_10;/** * No7_ReverseInteger * https://leetcode.com/problems/reverse-integer/ * * @Author zc * @Create 2016-06-27-19:55 */public class No7_ReverseInteger {    public int reverse(int x) {        long res = 0;        do {            res = res * 10 + x % 10;            if(integerOverflow(res)) {                return 0;            }            x /= 10;        } while(x != 0);        return (int) res;    }    private boolean integerOverflow(long a) {        return a > Integer.MAX_VALUE || a < Integer.MIN_VALUE;    }}