package leetcode1_10;/** * No9_PalindromeNumber * https://leetcode.com/problems/palindrome-number/ * * @Author zc * @Create 2016-06-30-15:59 */public class No9_PalindromeNumber {    public boolean isPalindrome(int x) {        if(x < 0) {            return false;        }        long y = x;        long len = 1;        while(y / len > 0) {            len *= 10;        }        len /= 10;        while(y != 0) {            long f = y / len;            long l = y % 10;            if(f != l) {                return false;            }            y = (y % len) / 10;            len /= 100;        }        return true;    }}